import MyFooter from "../../components/MyFooter/MyFooter"
import MyMap from "../../components/MyMap/MyMap"
import MyHeader from "../../components/header/MyHeader"
import styles from "./Basket.module.css"
import card_img from "../../assets/img/zatychka.png"
import { useEffect, useState } from "react"
import axios from "axios"

const Basket = ({basketId}) => {

    const [basketItems, setBasketItems] = useState([]);

    useEffect(() => {
        const fetchBasketItems = async () => {
            const result = await axios.get('http://localhost:5000/api/basket/?basketId=1');
            setBasketItems(result.data);
        };

        fetchBasketItems()
    }, [basketId])

    return(
        <div>
            <MyHeader/>
            <ul className={styles.main_basket}>
                {basketItems.map(item => (
                    <li className={styles.basket_card}>
                        <img src={card_img} alt="" />
                        <div className={styles.card_txt}>
                            <h1>Rose</h1>
                            <p>Увлажняющий крем идеально подходит для повседневного ухода за молодой кожей. <br /> Крем равномерно распределяется по поверхности благодаря легкой текстуре, обеспечивает глубокое увлажнение, регенерацию клеток. </p>
                            <div className={styles.basket_price}>
                                <h2>890 ₽</h2>
                                
                            </div>
                        </div>
                    </li>
                ))}
            </ul>
            <MyMap/>
            <MyFooter/>
        </div>
    )
}

export default Basket
