import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';
import SwiperCore, { Navigation, Thumbs } from 'swiper';

SwiperCore.use([Navigation, Thumbs]);

const Slider = () => {
  const [thumbsSwiper, setThumbsSwiper] = useState(null);

  return (
    <div>
      <h2>Product Slider</h2>
      <Swiper
        spaceBetween={10}
        navigation
        thumbs={{ swiper: thumbsSwiper }}
        className="main-slider"
      >
        <SwiperSlide>
          <img src="path_to_image1.jpg" alt="High" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image2.jpg" alt="Rest" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image3.jpg" alt="Rose" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image4.jpg" alt="Milk" />
        </SwiperSlide>
        {/* Add more slides as needed */}
      </Swiper>

      <Swiper
        onSwiper={setThumbsSwiper}
        spaceBetween={10}
        slidesPerView={4}
        freeMode
        watchSlidesProgress
        className="thumbs-slider"
      >
        <SwiperSlide>
          <img src="path_to_image1.jpg" alt="High" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image2.jpg" alt="Rest" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image3.jpg" alt="Rose" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="path_to_image4.jpg" alt="Milk" />
        </SwiperSlide>
        {/* Add more slides as needed */}
      </Swiper>
    </div>
  );
};

export default Slider;
